export default class PasswordPlugin extends Plugin {
	settings: PasswordSettings;
	selectedContent: string;
	doc: any;

	correctDecryptedFormat = (content: string) => {
		return content.startsWith(START_TAG) && content.endsWith(END_TAG);
	}


	getSelectedText = (): string => {
		const mdView = this.app.workspace.activeLeaf.view as MarkdownView;
		this.doc = mdView.editor;
		if(!mdView) {return}

		const selectedText = this.doc.getSelection();
		this.selectedContent = selectedText.trim();
	}

	encryptTextInPlace = (content: string, password: string) => {
		try {
			const encrypted =  AES.encrypt(content, password).toString();
			if (encrypted === null || encrypted.length <= 0) {
				throw new Error("Encrypted string is either null or empty.");
			}
			this.doc.replaceSelection(START_TAG+encrypted+END_TAG);
		} catch (error) {
			new Notice('Error encrypting! Logging error to dev console now.');
			console.log(error.toString());
		}
	}

	decryptTextInPlace = (content: string, password: string) => {
		try {
			const decrypted = AES.decrypt(content, password).toString(enc.Utf8);
			if (decrypted === null || decrypted.length <= 0) {
				throw new Error("Decrypted string is either null or empty. Did you try to decrypt something that wasn't encrypted?");
			}
			this.doc.replaceSelection(decrypted);
		} catch (error) {
			new Notice('Error decrypting! Logging error to dev console now.');
			console.log(error.toString());
		}
	}

	encryptSelection = () => {
		this.getSelectedText();
		if (this.settings.password.length > 0) {
			return this.encryptTextInPlace(this.selectedContent, this.settings.password);
		} else {
			new CryptModal(this.app, "Encrypt Content", "Encrypt",
				(password: string) => {this.encryptTextInPlace(this.selectedContent, password)}).open();
		}
	}

	decryptSelection = () => {
		this.getSelectedText();
		let contentToDecrypt = '';
		if (this.correctDecryptedFormat(this.selectedContent)) {
			contentToDecrypt = this.selectedContent.substring(START_TAG.length, this.selectedContent.length - END_TAG.length);
		} else {
			new Notice("Error decrypting! Make sure you also select the <encrypted> tag if available.");
			return;
		}
		if (this.settings.password.length > 0) {
			this.decryptTextInPlace(contentToDecrypt, this.settings.password);
		} else {
			new CryptModal(this.app, "Decrypt Content", "Decrypt",
				(password: string) => {this.decryptTextInPlace(contentToDecrypt, password)}).open();
		}
	}

	registerClearPasswordEvent = () => {
		this.registerEvent(
			this.app.workspace.on("quit", () => {
				if (this.settings.clearPasswordOnExit) {
					this.settings.password = '';
					this.saveSettings().then(
						() => new Notice('Password cleared!')
					).catch((error) => {
						new Notice('Error clearing password! Logging error to dev console now.');
						console.log(error.toString());
					})
				}
			})
		);
	};

	async onload() {
		await this.loadSettings();

		this.addCommand({
			id: 'encrypt-selection',
			name: 'Encrypt selection',
			callback: this.encryptSelection,
		});

		this.addCommand({
			id: 'decrypt-selection',
			name: 'Decrypt selection',
			callback: this.decryptSelection,
		});

		this.addSettingTab(new SettingsTab(this.app, this));

		this.registerClearPasswordEvent();

		if (this.settings.password.length <= 0 && this.settings.askForPasswordOnLoad) {
			new CryptModal(this.app, "Add password", "Save", async (password: string) => {
				this.settings.password = password;
				await this.saveSettings().then(
					() => new Notice('Password saved!')
				).catch((error) => {
					new Notice('Error saving password! Logging error to dev console now.');
					console.log(error.toString());
				});
			}).open();
		}
	}

	async loadSettings() {
		this.settings = Object.assign({}, DEFAULT_SETTINGS, await this.loadData());
	}

	async saveSettings() {
		await this.saveData(this.settings);
	}
}

class CryptModal extends Modal {
	onCloseCallback: any;
	callToAction: string;
	title: string;

	constructor(app: App, title: string, callToAction: string, onCloseCallback: any) {
		super(app);
		this.callToAction = callToAction;
		this.onCloseCallback = onCloseCallback;
		this.title = title;
	}


	renderContents(contentEl: any) {
		const passwordInput = contentEl.createEl('input');
		passwordInput.id = "password-input";
		passwordInput.innerText = EMPTY_PASSWORD;
		passwordInput.addClass('modal-input');
		const confirmButton = contentEl.createEl('button');
		confirmButton.innerText = this.callToAction;
		confirmButton.addClass('modal-submit-button');
		confirmButton.onClickEvent(() => {
			const password = (<HTMLInputElement>document.getElementById("password-input")).value;
			this.onCloseCallback(password);
			this.onClose();
			this.close();
		})
	}

	onOpen() {
		let {contentEl} = this;
		this.titleEl.setText(this.title);
		this.renderContents(contentEl);
	}

	onClose() {
		let {contentEl} = this;
		contentEl.empty();
	}
}

class SettingsTab extends PluginSettingTab {
	plugin: PasswordPlugin;

	constructor(app: App, plugin: PasswordPlugin) {
		super(app, plugin);
		this.plugin = plugin;
	}

	display(): void {
		let {containerEl} = this;

		containerEl.empty();

		containerEl.createEl('h2', {text: 'Password settings'});

		new Setting(containerEl)
			.setName('Password')
			.setDesc('Store your password here to avoid entering it each time you encrypt or decrypt some text. This is not the most secure option, but it is convenient.')
			.addText(text => text
				.setPlaceholder('Enter your password')
				.setValue(this.plugin.settings.password || '')
				.onChange(async (value) => {
					this.plugin.settings.password = value;
					await this.plugin.saveSettings();
				}));

		new Setting(containerEl)
			.setName('Ask for password on program start (WARNING: this stores password in plaintext in settings, as seen above.)')
			.setDesc('If a password isn\'t set, pop open a modal when Obsidian or the plugin opens so you can input your password.' +
				' As noted above: this stores the password in plaintext in the settings. This is obviously not secure. However, ' +
				'you can turn on "Clear password on exit" below to delete the stored password when you close Obsidian.')
			.addToggle(toggle => toggle.setValue(this.plugin.settings.askForPasswordOnLoad)
				.onChange(async (value) => {
					this.plugin.settings.askForPasswordOnLoad = value;
					await this.plugin.saveSettings();
				}));

		new Setting(containerEl)
			.setName('Clear password on exit')
			.setDesc('Clears stored password in settings when you close Obsidian or the plugin.')
			.addToggle(toggle => toggle.setValue(this.plugin.settings.clearPasswordOnExit)
				.onChange(async (value) => {
					this.plugin.settings.clearPasswordOnExit = value;
					await this.plugin.saveSettings();
				}));


		new Setting(containerEl)
			.setName('Create emergency decrypt file')
			.setDesc("Creates a javascript file in vault's root folder with all the code necessary to decrypt your work in the event of " +
				"an emergency. NOTE: depending on your Obsidian settings, you may not see the file in Obsidian, as it is not a markdown file.")
			.addButton(button => {
				button.setButtonText("Create file");
				button.onClick(async () => {
					console.log(normalizePath(this.app.vault.getRoot().path) + '/' + BACKUP_FILE_NAME);
					await this.app.vault.create(
						normalizePath(this.app.vault.getRoot().path) + '/' + BACKUP_FILE_NAME, BACKUP_FILE_CONTENTS
					).then(() => {
						new Notice("File created in vault root! It's a javascript file, so it may not be visible in Obsidian depending on your settings. But it is there.");
					}).catch((error) => {
						new Notice("Error creating file! Logging error to dev console now.");
						console.log(error.toString());
					});
				})
			});
	}
}
